CREATE TABLE questao
(
    id         SERIAL,
    questao    VARCHAR(2000)          NOT NULL,
    assunto_id BIGINT UNSIGNED       NOT NULL,
    orgao_id   BIGINT UNSIGNED       NOT NULL,
    banca_id   BIGINT UNSIGNED       NOT NULL,
    excluido   BOOLEAN DEFAULT FALSE NOT NULL,
    PRIMARY KEY (id),
    FOREIGN KEY (assunto_id)
        REFERENCES assunto (id)
        ON DELETE RESTRICT,
    FOREIGN KEY (orgao_id)
        REFERENCES orgao (id)
        ON DELETE RESTRICT,
    FOREIGN KEY (banca_id)
        REFERENCES banca (id)
        ON DELETE RESTRICT
) ENGINE = InnoDB;

